// ==UserScript==
// @name           autoCopy_menu.uc.js
// @namespace      ithinc#mozine.cn
// @description    可控自动复制,工具栏菜单版
// @include        main
// @compatibility  Firefox 3.0.x
// @author         ithinc, iwo
// @charset        UTF-8
// @version        LastMod 2016-09-15 by skofkyo
// @note           2016-09-15 IntPref==>BoolPref
// @note           2014-08-31 修正一下某些输入框也复制的问题
// @note           2014-04-12 修改原脚本为自用版
// @Note           https://g.mozest.com/redirect.php?goto=findpost&pid=299093&ptid=42980
// @homepageURL    https://github.com/defpt/userChromeJs/tree/master/autoCopy
// ==/UserScript==
(function() {

    var lastSelection = "";

    function autocopyStart(e) {
        lastSelection = getBrowserSelection();
    }

    function autocopyStop(e) {
        var autocopyState = gPrefService.getBoolPref("userChrome.autocopy.autocopyState");
        var selection = getBrowserSelection();
        //增加判断是否在输入框或按下功能键
        var exceptTarget = (e.target.localName == "textarea" || e.target.type == "input" || e.target.type == "text" || e.target.type == "password" || e.target.type == "email" || e.target.contentEditable == "true");
        var exceptoriginalTarget = (!e.originalTarget.ownerDocument || e.originalTarget.ownerDocument.designMode == "off" || e.originalTarget.ownerDocument.designMode == "undefined");
        var exceptAlternativeKey = (e.ctrlKey || e.altKey);
        var except = (exceptTarget && exceptoriginalTarget && !exceptAlternativeKey);

        if (autocopyState && selection && selection != lastSelection && !except) {
            goDoCommand('cmd_copy');
        }
    }

    gBrowser.mPanelContainer.addEventListener("mousedown", autocopyStart, false);
    gBrowser.mPanelContainer.addEventListener("mouseup", autocopyStop, false);

    var item = $("menu_ToolsPopup").appendChild(document.createElement("menuitem"));
    item.setAttribute("id", "autocopy-menuitem");
    //item.setAttribute("class", "menuitem-iconic");
    item.setAttribute("oncommand", '\
        var autocopyState = gPrefService.getBoolPref("userChrome.autocopy.autocopyState");\
        gPrefService.setBoolPref("userChrome.autocopy.autocopyState", autocopyState ? false : true);\
    ');

    function refreshStatus() {
        if (!gPrefService.getPrefType("userChrome.autocopy.autocopyState"))
            gPrefService.setBoolPref("userChrome.autocopy.autocopyState", true);
        var autocopyState = gPrefService.getBoolPref("userChrome.autocopy.autocopyState");
        var label, src;
        if (autocopyState) {
            label = "自动复制 已启用";
            //src = "data:image/png;base64,";
        } else {
            label = "自动复制 已禁用";
            //src = "data:image/png;base64,";
        }
        var item = $("autocopy-menuitem");
        item.setAttribute("label", label);
        item.setAttribute("type", "checkbox");
        item.setAttribute("checked", autocopyState);
        //item.setAttribute("src", src);
    }
    refreshStatus();

    function $(id) {
        return document.getElementById(id);
    }

    var observer = {
        observe: function(subject, topic, prefName) {
            refreshStatus();
        }
    };
    gPrefService.QueryInterface(Ci.nsIPrefBranchInternal).addObserver("userChrome.autocopy.autocopyState", observer, false);
})();